mapper.select:
select s.*,sh.playCount 
from song s
left outer join (
	select songNo, count(songNo) as playCount
	from song_play_history
	group by songNo
) sh on s.songNo = sh.songNo
where s.type=#{type}
order by sh.playCount desc, s.createdDate desc
limit 0, 100;

mapper.update:
update song
set thumbnailURL=#{thumbnailURL}
where songNo=#{songNo}

mapper.select:
select s.*, sh.playCount from song s left outer join ( select songNo, count(songNo) as playCount from song_play_history group by songNo ) sh on s.songNo = sh.songNo where s.title like concat('%',#{keyword}, '%')
and s.type=#{type}
order by s.createdDate desc limit 0, 20;
