mapper.insert:
insert into play_list_master(Name, tempUserNo, categoryNo, shareYN, createdDate)
values(#{Name}, #{tempUserNo}, #{categoryNo}, 'N', NOW())

	select LAST_INSERT_ID();

insert into song(playListNo, title, singer, youtubeNo1, youtubeNo2, createdDate)
values(#{playListNo}, #{title}, #{singer}, #{youtubeNo1}, #{youtubeNo2}, NOW())

	select LAST_INSERT_ID();

mapper.select:
select * from search_history
where keyword = #{keyword} and expireDate > NOW()
limit 0,1;
